      ]),
      ZikerResponse(63, 'عند الفراغ من الطعام', [
        HadithResponse(
            1,
            '- "مَنْ أكَلَ طعاماً ثم قال : الحَمْدُ لله الذي أطْعَمني هذا الطعامَ ورَزقنيهِ مِنْ غَير حوْلٍ منِّي ولا قُوة غُفِر لهُ ما تقدم من ذَنبه" .(1)',
            '(1) حسن (صحيح سنن أبي داود 2/760)',
            1,
            0),
        HadithResponse(
            2,
            '- "الحمدُ لله الذي أطَعمَ وسَقَى وسَوَّغَهُ وجَعَل لهُ مَخْرَجاً" .(1)',
            '(1) صحيح (صحيح سنن أبي داود 2/730)',
            1,
            0),
        HadithResponse(
            3,
            '- "الحمدُ لله كثيراً طيباً (1) مباركاً فيهِ، غيْرَ مَكْفيٍ (2) ، ولا مُوَدَع (3) ، ولا مُستَغنَى (4) عَنْهُ رَبَّنا" . (5)',
            '(1) ( طيبا ) خالصا\n(2) ( غير مكفي ) أي ما أكلناه ليس كافيا عما بعده بل نعمك مستمرة علينا غير منقطعة طول أعمارنا .\n(3) ( ولا مودع ) من الوداع أي ليس آخر طعامنا\n(4) لا يستغني عنه أحد .\n(5) (روه البخاري ـ الفتح 7/148)',
            1,
            0),
        HadithResponse(
